import { useNavigation } from "@react-navigation/native";
import { useEffect, useState } from "react";
import {
  Dimensions,
  Image,
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
} from "react-native";
import Icon from "react-native-vector-icons/MaterialCommunityIcons";
import { formatDateTime } from "../utils/utils";

const WIDTH = Dimensions.get("window").width;
const HEIGHT = Dimensions.get("window").height;

const defaultIcon =
  "data:image/jpeg;base64,";

export default function TaskCard({ task }) {
  const navigation = useNavigation();
  const [taskDetail, setTaskDetail] = useState(task || {});

  useEffect(() => {
    setTaskDetail(task);
  }, [task]);

  return (
    <TouchableOpacity
      style={styles.taskCard}
      onPress={() => navigation.navigate("DetailScreen", { task: task })}
    >
      <View>
        <Image
          source={{
            uri: (taskDetail?.images && taskDetail?.images[0]) || defaultIcon,
          }}
          style={styles.taskCardImg}
          resizeMode="cover"
        />
      </View>
      <View style={styles.taskCardInfo}>
        <Text style={styles.taskCardInfoTitle}>{taskDetail?.name}</Text>
        <Text style={styles.taskCardInfoLimited}>
          Price: {taskDetail?.price}
        </Text>
        <Text style={styles.taskCardInfoLimited}>
          Address: {taskDetail?.description}
        </Text>
      </View>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  taskCard: {
    flex: 1,
    flexDirection: "row",
    padding: 12,
    borderRadius: 12,
    margin: 12,
    gap: 12,
    backgroundColor: "white",

    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,

    elevation: 5,
  },
  taskCardImg: {
    height: "100%",
    width: HEIGHT * 0.1,
    minHeight: HEIGHT * 0.1,
    borderWidth: 1,
  },
  taskCardInfo: {
    position: "relative",
    flex: 1,
    gap: 2,
  },
  taskCardInfoTitle: {
    fontWeight: "bold",
  },
  taskCardInfoFavor: {
    position: "absolute",
    right: 0,
    bottom: 0,
    padding: 8,
    borderWidth: 1,
    borderRadius: 12,
    borderColor: "red",
  },
  taskCardInfoLimited: {
    textTransform: "capitalize",
  },
});
